// import logo from './logo.svg';
import './App.css';
import React from "react";
import RecipePreview from './components/RecipePreview.tsx';
import RecipeData from "./RecipeData.json";
import Home from "./Home.js";
import Recipe from "./Recipe.tsx";
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Nav from "./Nav.tsx";
import About from "./About.tsx";
import { useState } from 'react';


// const [cart, setCart] = useState();

// const addToCart = (productId, variantInfo) => {

//   if(variantInfo) {
//       commerce.cart.add(productId, 1, variantInfo)
//           .then(res => {
//               setCart(res.cart)
//           })
//   } else {
//       window.alert('Please select a quantity.')
//   }


const { MongoClient, ServerApiVersion } = require('mongodb');
const uri = "mongodb+srv://recipes:Ho70Ox7Al8aCb4sB@cluster0.tlzcb9k.mongodb.net/?retryWrites=true&w=majority";

// Create a MongoClient with a MongoClientOptions object to set the Stable API version
const client = new MongoClient(uri, {
  serverApi: {
    version: ServerApiVersion.v1,
    strict: true,
    deprecationErrors: true,
  }
});

async function run() {
  try {
    // Connect the client to the server	(optional starting in v4.7)
    await client.connect();
    // Send a ping to confirm a successful connection
    await client.db("admin").command({ ping: 1 });
    console.log("Pinged your deployment. You successfully connected to MongoDB!");
  } finally {
    // Ensures that the client will close when you finish/error
    await client.close();
  }
}
run().catch(console.dir);


function App() {


  return (
    <div className="container">
      <BrowserRouter>
        <Nav />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/recipe/:name" element={<Recipe />} />
        </Routes>
      </BrowserRouter>
    </div>


  );
}

export default App;
